-- Import All Clubs from clubs.js into Supabase
-- Run this AFTER running simple-schema.sql
-- This will insert all 52 clubs/organizations into the organizations table

-- Function to escape single quotes for SQL
-- We'll use PostgreSQL's dollar-quoting or escape manually

ON CONFLICT (name) DO UPDATE SET
  description = EXCLUDED.description,
  category = EXCLUDED.category,
  website = EXCLUDED.website,
  email = EXCLUDED.email,
  image = EXCLUDED.image;

